import base64
import struct
print(struct.pack('>I', 10240099))
print(struct.unpack('>I', b'\x00\x9c@c'))

bmp_data = base64.b64decode('')
def bmp_info(data):
    result = struct.unpack('<ccIIIIIIHH', data[:30])
    print(result)
    if result[0]+result[1] in (b'BM',b'BA'):
        return {
            'width':result[6],
            'height':result[7],
            'color':result[9]
        }
    else:
        return {
            'width':0,
            'height':0,
            'color':0
        }

bi = bmp_info(bmp_data)
assert bi['width'] == 28
assert bi['height'] == 10
assert bi['color'] == 16
print('ok')